import React, { useState } from 'react';

function App() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Effectuer les validations nécessaires sur l'e-mail et le mot de passe

    // Envoyer une requête au serveur pour vérifier les informations de connexion
    // Vous pouvez utiliser l'API Fetch ou une bibliothèque comme Axios

    // Exemple d'utilisation de l'API Fetch
    fetch('http://localhost:3000/api/auth/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ email, password }),
    })
      .then((response) => response.json())
      .then((data) => {
        // Gérer la réponse du serveur
        if (data.success) {
          // Authentification réussie, effectuer les actions appropriées (redirection, etc.)
        } else {
          // Authentification échouée, afficher un message d'erreur ou effectuer les actions appropriées
        }
      })
      .catch((error) => {
        // Gérer les erreurs de la requête
        console.error('Error:', error);
      });
  };

  return (
    <div>
      <h2>Portail de connexion</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="email">E-mail:</label>
          <input type="email" id="email" value={email} onChange={handleEmailChange} />
        </div>
        <div>
          <label htmlFor="password">Mot de passe:</label>
          <input type="password" id="password" value={password} onChange={handlePasswordChange} />
        </div>
        <button type="submit">Se connecter</button>
      </form>
    </div>
  );
}

export default App;
