import React, { useEffect, useState } from 'react';
import moment from 'moment';

function App() {
  const [messages, setMessages] = useState([]);
  const storedEmail = localStorage.getItem('email');
  const storedToken = localStorage.getItem('token');
  console.log(storedEmail);
  console.log(storedToken);

  useEffect(() => {
    // Envoyer une requête au serveur pour récupérer les messages associés à l'e-mail
    fetch(`http://localhost:3000/api/message?email=${storedEmail}`)
      .then(response => response.json())
      .then(data => {
        // Récupérer les messages triés par ordre d'horaire depuis les données
        const sortedMessages = data;
        setMessages(sortedMessages);
      })
      .catch(error => {
        // Gérer les erreurs
        console.error(error);
      });
  }, [storedEmail]);

  return (
    <div>
      <h2>Liste des messages</h2>
      <ul>
        {messages.map(message => (
          <li key={message._id}>
            <p>Date: {moment(message.date).format('YYYY-MM-DD')} {message.time}</p>
            <p>Contenu: {message.message}</p>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
