import React, { useEffect, useState } from 'react';

function App() {
  const [data, setData] = useState([]);
  const [formData, setFormData] = useState({
    id: '',
    names: '',
    emails: ''
  });

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = () => {
    fetch('http://localhost:3000/api/category')
      .then(response => response.json())
      .then(result => {
        setData(result);
      })
      .catch(error => {
        console.error('Erreur lors de la requête :', error);
        // Gérer l'erreur
      });
  };

  const handleChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const updateData = id => {
    fetch(`http://localhost:3000/api/category/modify/${id}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(formData)
    })
      .then(response => {
        if (response.ok) {
          // La mise à jour a réussi, mettre à jour les données
          fetchData();
        } else {
          // La mise à jour a échoué, gérer l'erreur
          console.error('Erreur lors de la mise à jour de la donnée');
          // Gérer l'erreur
        }
      })
      .catch(error => {
        console.error('Erreur lors de la requête :', error);
        // Gérer l'erreur
      });
  };

  return (
    <div>
      <h1>Données</h1>
      <ul>
        {data.map(item => (
          <li key={item._id}>
            {item.names} - {item.emails} - {item._id}
            <input
              type="text"
              name="name"
              value={formData.names}
              onChange={handleChange}
            />
            <input
              type="text"
              name="email"
              value={formData.emails}
              onChange={handleChange}
            />
            <button onClick={() => updateData(item._id)}>Modifier</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
